48.text:
For React applications, here&#x27;s a custom hook for file uploads:




  const [isUploading, setIsUploading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [error, setError] = useState&lt;string | null&gt;(null);

  const uploadFile = async (file: File): Promise&lt;string&gt; =&gt; {
    setIsUploading(true);
    setProgress(0);
    setError(null);

    try {
      const uri = await clientUploadToArweave(file, setProgress);
      setIsUploading(false);
      return uri;
    } catch (err) {
      setError(err instanceof Error ? err.message : &quot;Upload failed&quot;);
      setIsUploading(false);
      throw err;
    }
  };

  return {
    uploadFile,
    isUploading,
    progress,
    error,
  };
};

// Usage in component
const MyComponent = () =&gt; {
  const { uploadFile, isUploading, progress, error } = useArweaveUpload();

  const handleFileUpload = async (
    event: React.ChangeEvent
  ) =&gt; {
    const file = event.target.files?.[0];
    if (file) {
      try {
        const uri = await uploadFile(file);
        console.log(&quot;Uploaded to:&quot;, uri);
      } catch (err) {
        console.error(&quot;Upload failed:&quot;, err);
      }
    }
  };

  return (
    &lt;div&gt;
      &lt;input type=&quot;file&quot; onChange={handleFileUpload} /&gt;
      {isUploading &amp;&amp; (
        &lt;div&gt;
          &lt;p&gt;Uploading... {progress}%&lt;/p&gt;
          &lt;progress value={progress} max={100} /&gt;
        &lt;/div&gt;
      )}
      {error &amp;&amp; &lt;p style={{ color: &quot;red&quot; }}&gt;Error: {error}&lt;/p&gt;}
    &lt;/div&gt;
  );
};

